////////////////////////////////////////////////////////////////////////////////
//模板使用说明
////////////////////////////////////////////////////////////////////////////////

//图标必须是正方形的，不是正方形也会被压成正方形，注意希望。

////////////////////////////////////////////////////////////////////////////////
//模板参数控制
////////////////////////////////////////////////////////////////////////////////

//默认卡片颜色
var defaultColor = "#000";
//卡片图标存储文件夹(相对index.html的位置)
var iconPath = './img/icons';
//默认卡片图标(相对瓷砖图标存储文件夹的文件夹文件名)
var defaultIcon = '#';
//默认产品地址
var defaultProductURl = '#';
//默认产品名
var defaultProductName = "Unknown Technology";

////////////////////////////////////////////////////////////////////////////////
//其余资源及参数初始化
////////////////////////////////////////////////////////////////////////////////

//z-index层级堆叠
var zIndex = 100;

//下面是两个比较丧病的东西，你没有看见它们对么/w\
var pix1x1ImgRes = 'data:image/png;base64,';
var pix2x1ImgRes = 'data:image/png;base64,';

////////////////////////////////////////////////////////////////////////////////
//页面控制方法
////////////////////////////////////////////////////////////////////////////////

var windows = {
    cardBackgroundWidthFit: function() {
        $("#main>li").each(function() {
            var mainWidth = $("#main").width();
            var backgroundWidth = (mainWidth / 3) * 0.4;
            var iconSize = backgroundWidth + "px";
            $(this).css("background-size", iconSize + " " + iconSize);
        });
    }
};

////////////////////////////////////////////////////////////////////////////////
//页面效果控制脚本
////////////////////////////////////////////////////////////////////////////////

$(document).ready(function() {
    //调整card宽度
    windows.cardBackgroundWidthFit();
    //将各个卡片的长宽比拘束好
    $("#main>li").each(function() {

        //调整尺寸
        if ($(this).attr("class") === "r1p3")
            ImgRes = pix1x1ImgRes;
        else if ($(this).attr("class") === "r2p3")
            ImgRes = pix2x1ImgRes;
        $(this).append('<img src="' + ImgRes + '" />');

        //调整背景色
        if (!$(this).attr("color"))
            $(this).attr("color", defaultColor);

        $(this).css("background-color", $(this).attr("color"));

        //调整优先级
        $(this).css("z-index", zIndex);
        zIndex = zIndex - 1;

        //添加icon
        if (!$(this).attr("iconURl") || $(this).attr("iconURl") === "")
            $(this).attr("iconURl", defaultIcon);

        var iconName = $(this).attr("iconURl");
        var iconURl = "url(" + iconPath + '/' + iconName + ")";
        $(this).css("background-image", iconURl);

        //注入内容div
        if (!$(this).attr("productName") || $(this).attr("productName") === "")
            $(this).attr("productName", defaultProductName);

        $(this).append('<div class="content_box">' + $(this).attr("productName") + '</div>');

        //注入鼠标特效元素
        $(this).append('<div class="light_effect"></div>');

        //卡片点击监听
        if (!$(this).attr("productURl") || $(this).attr("productURl") === "")
            $(this).attr("productURl", defaultProductURl);

        $(this).on("click", function(evt) {
            var effectX = evt.clientX - ($(this).offset().left - $(document).scrollLeft());
            var effectY = evt.clientY - ($(this).offset().top - $(document).scrollTop());
            $(this).children(".light_effect").css({
                "display": "block",
                "left": effectX,
                "top": effectY
            });
            $(this).children(".light_effect").addClass("light_effect_action");
            var them = this;
            setTimeout(function() {
                $(them).children(".light_effect").hide();
                $(them).children(".light_effect").removeClass("light_effect_action");
                
                window.location.href=$(them).attr("productURl");
            }, 500);
        });
    });

    //页宽改变时调整icon尺寸
    $(window).on("resize", function() {
        windows.cardBackgroundWidthFit();
    });

    //帕秋莎GO！
    $("#go_main").on("click", function() {
        $(document.body).animate({scrollTop: $("#main").offset().top}, 200);
    })

    //底部切换语言
    $("#lang").on("mouseover", function() {
        $("#lang>ul").css({
            "height": "auto",
            "opacity": "1"
        });
    });

    $("#lang>ul").on("mouseleave", function() {
        $("#lang>ul").css({
            "height": "0",
            "opacity": "0"
        });
    });

    console.log('///////////////////////////////////////////////////////////////////////');
    console.log('                           FUCK YOU GOOGLE                             ');
    console.log('                                    By Losses Don                      ');
    console.log('///////////////////////////////////////////////////////////////////////');
    console.log('Mail:Minecard@qzworld.net                                              ');
    console.log('This template was licensed under CC-BY-SA 3.0.');
});